$(document).ready(function(){$("body").on("click","#shadow, #alert_shadow, #confirm_shadow, .modal_close_link",function(){if($("#alert_modal").length!=0){closeMyAlert()}else{if($("#confirm_modal").length!=0){closeMyConfirm()}else{closeModal()}}});$("body").keyup(function(a){if(a.keyCode=="27"){if($("#alert_modal").length!=0){closeMyAlert()}else{if($("#confirm_modal").length!=0){closeMyConfirm()}else{closeModal()}}}});$(window).resize(function(){$("#shadow").height($(document).height())})});function openModal(a){if($("div.opened_window").length!==0){$("div.opened_window").fadeOut(300)}else{$("#shadow").height($(document).height()).fadeIn(300)}$("#"+a).addClass("opened_window");modalPosition();$("#"+a).fadeIn(300)}function closeModal(){$("div.opened_window").fadeOut(300);$("#shadow").fadeOut(300);$("div.opened_window").removeClass("opened_window");$(".login_err").text("")}function myAlert(d,c,b){if(typeof(c)=="undefined"){c=_("Message")}if(typeof(b)=="undefined"){b="default"}var a='<div id="alert_modal" class="modal_window small_modal_window alert_type_'+b+'"><div class="modal_wrapper"><div class="modal_close"><a href="javascript:void(0)" title="'+_("Close")+'" class="modal_close_link" onclick="closeMyAlert()">&times;</a></div><div class="modal_top"><div class="modal_title">'+c+'</div></div><div class="modal_content"><p class="text" id="modal_text">'+d+'</p><div class="modal_button"><a href="javascript:void(0)" class="btn btn-green" onclick="closeMyAlert()">'+_("Рћk")+'</a></div></div></div></div><div id="alert_shadow"></div>';if($("#alert_modal").length!=0){$("#alert_modal").remove()}$("body").append(a);$("#alert_shadow").height($(document).height()).css({opacity:"0",display:"block"});$("#alert_shadow").animate({opacity:"0.65"},500);$("#alert_modal").addClass("opened_window");modalPosition();$("#alert_modal").fadeIn(300)}function closeMyAlert(){$("#alert_modal").fadeOut(300).removeClass("opened_window");$("#alert_shadow").animate({opacity:"0"},500);setTimeout('$("#alert_shadow").css("display", "none");$("#alert_modal").remove();',500)}function myConfirm(e,c,a,d){if(typeof(d)=="undefined"){d=_("Message")}if(typeof(a)=="undefined"){a=_("OK")}if(typeof(e)=="undefined"){e=_("Are you sure?")}if(typeof(c)=="undefined"){c=myAlert(_("Nice guy! :)"))}var b='<div id="confirm_modal" class="modal_window small_modal_window"><div class="modal_wrapper"><div class="modal_close"><a href="javascript:void(0)" title="'+_("Close")+'" class="modal_close_link" onclick="closeMyConfirm()">&times;</a></div><div class="modal_top"><div class="modal_title">'+d+'</div></div><div class="modal_content"><div class="text" id="modal_text">'+e+'</div><div class="modal_button"><a href="javascript:void(0)" class="btn btn-blue btn-smaller" onclick="closeMyConfirm()">'+_("Cancel")+'</a><a href="javascript:void(0)" class="btn btn-orange btn-smaller" onclick="'+c+'">'+a+'</a></div></div></div></div><div id="confirm_shadow"></div>';if($("#confirm_modal").length!=0){$("#confirm_modal").remove()}$("body").append(b);$("#confirm_shadow").height($(document).height()).css({opacity:"0",display:"block"});$("#confirm_shadow").animate({opacity:"0.65"},500);$("#confirm_modal").addClass("opened_window");modalPosition();$("#confirm_modal").fadeIn(300)}function closeMyConfirm(){$("#confirm_modal").fadeOut(300).removeClass("opened_window");$("#confirm_shadow").animate({opacity:"0"},500);setTimeout('$("#confirm_shadow").css("display", "none");$("#confirm_modal").remove();',500)}function modalPosition(){var a=$("div.opened_window");var c=($(window).height()-a.height())/2;var b=($(window).width()-a.width())/2;a.css({top:c,left:b})}$(window).resize(function(){if($("div.opened_window").length!==0){modalPosition()}});